$(document).ready(function () { $("#customerList").DataTable({ language: { url: "//cdn.datatables.net/plug-ins/1.10.22/i18n/Turkish.json" }, initComplete: function () { $(this).removeClass('d-none');}});$("#addEditCustomer").on("show.bs.modal",function(n){var t=$(n.relatedTarget);$(this).find(".modal-title").text(t.data("title"));$(this).find(".modal-body").load(t.attr("href"),function(){$('input:radio[name="type"]').on("change",function(){$("#Name").prop("disabled",this.value==2);$("#Surname").prop("disabled",this.value==2);$("#CompanyTitle").prop("disabled",this.value==1);$('label[for="TaxNumber"]').text(this.value==1?"T.C. Kimlik No":"Vergi No")});$("#City_Name").on("change",function(){$.ajax({url:"/District/GetCityDistricts/"+$("#City_Name").val(),cache:!1,type:"GET",dataType:"json",success:function(n){$("#District_Name").empty().append($("<option disabled selected />").val("").text("-- Bir değer seçin --"));$.each(n,function(){$("#District_Name").append($("<option />").val(this.id).text(this.name))})},error:function(n,t,i){let r="";try{let t=jQuery.parseJSON(n.responseText);r=t.message}catch(u){r=n.responseText.length>0?n.responseText:i}console.log(r)}})})})})});